mapper.select:
select * from book t1,store t2

    t1.store_id=t2.store_id

        and t1.book_id=#{bookId}

select * from book t1,store t2,bookrelation t3,classification t4

    t1.store_id=t2.store_id and t1.book_id=t3.book_id and t3.class_id=t4.class_id

        and t1.book_name LIKE CONCAT('%',#{bookInfo.bookName},'%') or t1.author LIKE CONCAT('%',#{bookInfo.bookName},'%')


        and t1.book_price >= #{bookInfo.minBookPrice}


        and t1.book_price <= #{bookInfo.maxBookPrice}


        and t4.classname=#{bookInfo.classname}
